mapper.update:
update advertise_contract
set status = #{status}, gmt_modified = now()
where advertise_id = #{advertiseId}

update advertise
state=#{status,typeHandler=AdvertiseStatusTypeHandler}, price=#{price}, title=#{title}, content=#{content}, width=#{width}, height=#{height}, format=#{format}
where id =#{id}

update advertise
set profit = profit + #{addProfit}
where id = #{id}
